    <Container>
      <Title>Todo List Application With React JS & Firebase</Title>
      <AddTodo />
      {
        todos.map(todo => <Todo key={todo.key} name={todo.name} isCompleted={todo.isCompleted} />)
      }
      <Footer />
    </Container>
  );
}

export default App;
