import React from 'react'
import styled from "styled-components";
import AddTodo from './AddTodo';
import Footer from './Footer';

const Container = styled.div`
  max-width: 500px;
  width: 100%;
  min-height: 400px ;
  box-shadow: 5px 5px 4px gray;
  margin: 20px auto;
`

function App() {
  return (
    <Container>
      <Title>Todo List Application</Title>
      <AddTodo />
      <Footer />
    </Container>
  );
}

export default App;
