import React from "react";
import styled from "styled-components";
import { mobile } from "./responsive";

const Form = styled.form``;

const Input = styled.input`
  padding: 1rem;
  text-indent: 5px;
  width: 80%;
  border: none;
  outline: none;
  ${mobile({
      width : "70%",
      marginLeft : '10px'
  })}
`;

const Button = styled.button`
  padding: 1rem 1.6rem;
  margin-left: 5px;
  text-transform: uppercase;
  background-color: #5396e7;
  color: #fff;
  border: none;
  outline: none;
  cursor: pointer;
  font-weight: bold;
`;

const AddTodo = () => {
  return (
    <Form>
      <Input type="text" placeholder="Enter The New Todo"></Input>
      <Button>Add</Button>
    </Form>
  );
};

export default AddTodo;
