import React from "react";
import { FaPlus } from "react-icons/fa";
import styled from "styled-components";
import { mobile } from "./responsive";

const Form = styled.form``;

const Input = styled.input`
  padding: 1rem;
  text-indent: 2px;
  width: 80%;
  border: none;
  outline: none;
  margin-left: 15px;
  border-radius: 0;
  ${mobile({
    width: "71%",
    marginLeft: "12px",
    padding : "1.09rem 1rem"
  })}
`;

const Button = styled.button`
  padding: 1rem;
  margin-left: 3px;
  text-transform: uppercase;
  background-color: #5396e7;
  color: #fff;
  border: none;
  outline: none;
  cursor: pointer;
  font-weight: bold;
  ${mobile({
    padding: "1.0rem 1.5rem",
    fontSize : ".9rem"
  })}
`;

const AddTodo = () => {
  return (
    <Form>
      <Input type="text" placeholder="Enter The New Todo"></Input>
      <Button>Add</Button>
    </Form>
  );
};

export default AddTodo;
